Card 1.Back:
{{inputDefinition1}}
<hr id=answer>
<div id="answer-sentence">{{example1}}</div>

{{#coreMeaning}}
   <div class="text-block">Core Meaning</div>
      <span>{{coreMeaning}}</span>
{{/coreMeaning}}

<script>
(function() {
    const answerElement = document.getElementById('answer-sentence');
    const forms = [
        '{{vocabulary}}',
        '{{vocabularyPast}}',
        '{{vocabularyPastPerfect}}',
        '{{vocabularyIng}}',
        '{{vocabularyS}}'
    ];
    if (answerElement) {
        let text = answerElement.innerHTML;
        forms.forEach(form => {
            if (form && form.trim() !== '') {
                const regex = new RegExp('\\b' + form + '\\b', 'gi');
                text = text.replace(regex, '<span style="color: var(--orange); font-weight: bold;">' + form + '</span>');
            }
        });
        answerElement.innerHTML = text;
    }
})();
</script>

