:root {
  /* main color */
  --orange: #fb9f2a;
  --red: #941123;
  --medium-red: #5c061d;
  --dark-red: #2c030c;
  --light-blue: #31c2e2;
  --medium-blue: #0f58a7;
  --green: #24bc3c;
  --medium-green: #014a0f;
  --dark-green: #032c0a;
  --light-gray: #9f9f9f;
  --medium-gray: #434343;
  --dark-gray: #2b2b2b;

  /* blue gradient */
  --dark-blue-lighter: #24313b;
  --dark-blue-deeper: #030303;
  --blue-lighter: #177080;
  --blue-deeper: #032d55;
}

.card-cloze {
    font-family: arial;
    font-size: 20px;
    text-align: center;
    color: white;
}
.cloze {
    font-weight: bold;
    color: var(--orange);
}
.card-title {
   color: orange;
   padding: 10px 15px;
   border-radius: 4px 4px 0 0;
   font-size: 1.1em;
}
.card-content {
   line-height: 1.6;
   fron-size: 1.2em;
}
.centre-box {
   margin: 20px auto;
   padding: 15px 10px;
   border-radius: 6px;
   font-size: 90%;
   color: white;
   line-height: 1.6;
   cursor: pointer;
   text-align: center;
   display: block;
   width: fit-content;
   background-color: var(--medium-gray);
   transition: all 0.2s ease;
   font-weight: bold;
}

.centre-box:hover {
   transform: translateY(-2px);
}

.centre-box:active {
   transform: translateY(0);
}

.centre-box::before {
   content: "ℹ️ ";
   font-weight: bold;
   margin-right: 6px;
}

.card {
    font-family: arial;
    font-size: 20px;
    line-height: 1.5;
    text-align: center;
    color: black;
    background-color: white;
}

